def solve(n, x, pos):
    MOD = 10**9 + 7
    dp = [[0] * (n + 1) for _ in range(n + 1)]
    dp[0][0] = 1

    for i in range(1, n + 1):
        for j in range(i + 1):
            if j == 0:
                dp[i][j] = dp[i - 1][j]
            elif j == i:
                dp[i][j] = dp[i - 1][j - 1]
            else:
                dp[i][j] = (dp[i - 1][j] + dp[i - 1][j - 1]) % MOD

    return dp[n][pos]

def main():
    n, x, pos = map(int, input().split())
    print(solve(n, x, pos))

if True:
    main()
