def solve():
    r, s, p = map(int, input().split())
    dp = [[[0.0 for _ in range(p + 1)] for _ in range(s + 1)] for _ in range(r + 1)]
    dp[r][s][p] = 1.0
    for i in range(r, 0, -1):
        for j in range(s, 0, -1):
            for k in range(p, 0, -1):
                dp[i - 1][j][k] += dp[i][j][k] * (i * j * k / (i * j + i * k + j * k))
                dp[i][j - 1][k] += dp[i][j][k] * (i * j * k / (i * j + i * k + j * k))
                dp[i][j][k - 1] += dp[i][j][k] * (i * j * k / (i * j + i * k + j * k))
    print(dp[0][0][0], dp[0][0][1], dp[0][0][2])

if True:
    solve()
