def solve():
    n, k = map(int, input().split())
    tab_info = list(map(int, input().split()))
    e, s = 0, 0
    for i in range(n):
        if tab_info[i] == 1:
            e += 1
        else:
            s += 1
    total_sum = sum(tab_info) % k
    max_diff = 0
    for i in range(1, k):
        max_diff = max(max_diff, abs((total_sum + i) % k - (total_sum - i) % k))
    print(max_diff)

if True:
    solve()
