def solve():
    n, k = map(int, input().split())
    nn = n - k * (k + 1) // 2
    if nn < 0:
        print("NO")
        return
    a = [nn // k] * k
    for i in range(nn % k):
        a[i] += 1
    if nn != k - 1 or k == 1:
        print("YES")
        print(*a)
    else:
        a[1] -= 1
        a[-1] += 1
        print("YES")
        print(*a)

if True:
    solve()
